import React, { useState } from 'react';
import StudentList from '../components/StudentList';

const Dashboard = () => {
  // State to track which menu is clicked
  const [view, setView] = useState('dashboard');

  return (
    <div style={{ display: 'flex', height: '100vh', backgroundColor: '#f0f2f5' }}>
      
      {/* Sidebar */}
      <div style={{ width: '250px', backgroundColor: '#f5f5dc', padding: '20px', borderRight: '1px solid #ddd' }}>
        <h2 style={{ color: 'blue', marginBottom: '30px' }}>ADMIN</h2>
        
        <div style={{ marginBottom: '20px' }}>
          <p style={{ fontSize: '12px', color: '#999', fontWeight: 'bold' }}>DASH</p>
          <p 
            onClick={() => setView('dashboard')} 
            style={{ cursor: 'pointer', padding: '10px 0', color: view === 'dashboard' ? 'purple' : '#555', fontWeight: 'bold' }}
          >
            üìä Dashboard
          </p>
        </div>

        <div>
          <p style={{ fontSize: '12px', color: '#999', fontWeight: 'bold' }}>APPEARANCE</p>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            <li onClick={() => setView('students')} style={{ padding: '10px 0', cursor: 'pointer', color: view === 'students' ? 'purple' : '#555', fontWeight: 'bold' }}>
              üë• students
            </li>
            <li style={{ padding: '10px 0', cursor: 'pointer', color: '#555' }}>üìù Result</li>
            <li style={{ padding: '10px 0', cursor: 'pointer', color: '#555' }}>üîë Admin Change Password</li>
          </ul>
        </div>
      </div>

      {/* Main Content Area */}
      <div style={{ flex: 1, padding: '40px', overflowY: 'auto' }}>
        
        {view === 'dashboard' && (
          <>
            <div style={{ display: 'flex', gap: '20px', marginBottom: '40px' }}>
              <StudentList title="Regd users" count="10" />
              <StudentList title="Subject Listed" count="12" />
            </div>
            <h2>Hello Admin</h2>
            <div style={{ backgroundColor: 'white', padding: '20px', borderRadius: '10px', boxShadow: '0 2px 5px rgba(0,0,0,0.1)', maxWidth: '400px' }}>
              <h3 style={{ borderBottom: '1px solid #eee', paddingBottom: '10px' }}>Account Details</h3>
              <p style={{ marginTop: '10px' }}>üë§ Arun Kumar</p>
            </div>
          </>
        )}

        {view === 'students' && (
          <div>
            <h2>Student Management</h2>
            <button 
              style={{ backgroundColor: 'blue', color: 'white', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer', marginBottom: '20px' }}
              onClick={() => alert('Add Student Form Opening...')}
            >
              + Add student
            </button>
            <div style={{ backgroundColor: 'white', padding: '20px', borderRadius: '10px' }}>
              <p>Student list table goes here...</p>
            </div>
          </div>
        )}

      </div>
    </div>
  );
};

export default Dashboard;
